import React, {useState} from "react";
import "./App.css";
import UserInput from "./UserInput/UserInput";
import UserOutput from "./UserOutput/UserOutput";
import ValidationComponent from "./ValidationComponent/ValidationComponent"

const App = props => {
  const [userNameState, userNameSetter] = useState({
    userName : "Darcy Xie"
  })

  const [textState, textSetter] = useState({
    text : "Yes, it is!"
  })
  const setUserName = event => {
    userNameSetter({
      userName : event.target.value
    })
  }

  const setText = event => {
    textSetter({
      userName : event.target.value
    })
  }

  return (
    <div>
      <UserInput userName={userNameState.userName} text={textState.text} changeOutputUsername={setUserName} changeOutputText={setText}/>
      <UserOutput userName={userNameState.userName} text={textState.text}/>
      <ValidationComponent size={textState.text.length}/>
    </div>
  );
}

export default App;
